import useAxios from "../../utils/useAxios";
import { useState } from "react";

const Goals = (props) => {

	const [ name, setName ] = useState('');
	const [ importance, setImportance ] = useState(0);

	const api = useAxios();

	const objectives = props.objectives.map( objective => 
		<div key={objective.id}>
			<h3>{objective.name}</h3>
			<p>Importance: {objective.importance}</p>
		</div>
	);

	const handleCreateObjective = async (event) => {

		event.preventDefault();
		const body = {
			name: name,
			importance: importance
		}

		try {
            const response = await api.post("/objectives", body);
			setName('');
			setImportance('');
			props.setObjectives(response.data);

        } catch (error) {
            console.log(`Request failed: ${error.response.data.error_message}`);
		}
	}

	const handleNameChange = (event) => {
		setName(event.target.value);
	}

	const handleImportanceChange = (event) => {
		setImportance(event.target.value);
	}

	return (
		<div>
			<div>{objectives}</div>
			<div>
				<form onSubmit={handleCreateObjective}>
					<h3>Create Objective</h3>
					<div>
						<input type="text" placeholder="Name"
						value={name}
						onChange={handleNameChange}
						/>
					</div>
					<div>
						<input type="number" placeholder="Importance (1 to 5)"
						value={importance}
						onChange={handleImportanceChange}
						/>
					</div>
					<button type="submit">Create</button>
				</form>
			</div>
		</div>
	);
}

export default Goals;
