//import loginService from "../services/login";
import qs from "qs";
import axios from 'axios';

const Login = (props) => {

  const handleLogin = (event) => {
    event.preventDefault();
    try {
      loginService(props.emailAddress, props.password, props.setUsername);
    } catch (exception) {}
    props.setEmailAddress('');
    props.setPassword('');
  }

  const loginService = props => {
    const credentials = new URLSearchParams();
    credentials.append('username', props.emailAddress);
    credentials.append('password', props.password);
    const config = {
      headers: {
        'Content-Type': 'application/x-www-form-urlencoded'
      }
    }
    console.log(props.emailAddress);
    console.log(props.password);
    console.log(qs.stringify(credentials));
    const baseUrl = 'http://localhost:8080/login';

    axios.post(baseUrl, qs.stringify(credentials), config)
      .then((result) => {
        props.setUsername('login')
      })
      .catch((error) => {
        console.log(`Error: ${error}`)
      })
  }

  const handleEmailAddressChange = (event) => {
    console.log(props.emailAddress);
    props.setEmailAddress(event.target.value);
  }
  const handlePasswordChange = (event) => {
    props.setPassword(event.target.value);
  }

	return (
		<div className="login">
			<h3>Login</h3>

			<form onSubmit={handleLogin}>
				<div>
					<input type="text" placeholder="Email address"
					value={props.emailAddress}
					onChange={handleEmailAddressChange}
					/>
				</div>
				<div> 
					<input type="text" placeholder="Password"
					value={props.password}
					onChange={handlePasswordChange}
					/>
				</div>
				<button type="submit">login</button>
			</form>
		</div>
	)

}

export default Login;
