import { useState, useEffect } from "react";
import useAxios from "../../utils/useAxios";
import Objective from "./Objective";
import ToolBar from "./Toolbar";


const Objectives = (props) => {

	const [searchableObjectives, setSearchableObjectives] = useState(props.objectives);

	useEffect( () => {
        setSearchableObjectives(props.objectives);
    }, [props.objectives]);

	const api = useAxios();

	const handleCreateObjective = async () => {

		const newObjectiveTemplate = {
			title: "Title", 
			importance: "1" }

		try {
            const response = await api.post("/objectives", newObjectiveTemplate);

			props.setObjectives(props.objectives.concat(response.data));

        } catch (error) {
            console.log(`Request failed: ${error.response.data.error_message}`);
		}
	}

	const sortFunction = (a, b) => {
		const fa = a.title.toLowerCase();
		const fb = b.title.toLowerCase();
        const ia = a.importance;
        const ib = b.importance;

        if (ia < ib) return 1;
        else if (ia > ib) return -1;
        else if (ia === ib) {
            if (fa < fb) return -1;
            if (fa > fb) return 1;
        }
		else return 0;
	}

	const searchObjectives = (searchTerm) => {
		if (searchTerm === "" || searchTerm === undefined) {
			console.log("no search");
			return searchableObjectives
				.sort(sortFunction)
				.map(objective => 
					<Objective 
						key={objective.id}
						id={objective.id}
						title={objective.title}
						importance={objective.importance}
						setObjectives={props.setObjectives}
					/>);
		} else {
			console.log("search passed: " + searchTerm);
			return searchableObjectives
				.filter(objective => objective.title.toLowerCase().startsWith(searchTerm.toLowerCase()))
				.sort(sortFunction)
				.map(objective => 
					<Objective 
						key={objective.id}
						id={objective.id}
						title={objective.title}
						importance={objective.importance}
						setObjectives={props.setObjectives}
					/>);
		}
	}			


	return (
		<div>
			<ToolBar 
				handleCreateObjective={handleCreateObjective}
				searchObjectives={searchObjectives}
			/>
			<div className="objectives-container">
				{searchObjectives()}
			</div>
		</div>
	);
}

export default Objectives;
