import useAxios from "../../utils/useAxios";
import Objective from "./Objective";
import ToolBar from "./Toolbar";


const Objectives = (props) => {

	const api = useAxios();

	const handleCreateObjective = async () => {

		const newObjectiveTemplate = {
			title: "Title", 
			importance: "5" }

		try {
            const response = await api.post("/objectives", newObjectiveTemplate);

			props.setObjectives(props.objectives.concat(response.data));

        } catch (error) {
            console.log(`Request failed: ${error.response.data.error_message}`);
		}
	}

	const searchObjectives = (searchTerm) => {
		if (searchTerm === "" || searchTerm === undefined) {
			return props.objectives.map(objective => 
				<Objective 
					key={objective.id}
					id={objective.id}
					title={objective.title}
					importance={objective.importance}
					setObjectives={props.setObjectives}
				/>);
		} else {
			console.log("search passed");
			return props.objectives.filter(objective => objective.title.toLowerCase().startsWith(searchTerm.toLowerCase()))
			.map(objective => 
				<Objective 
					key={objective.id}
					id={objective.id}
					title={objective.title}
					importance={objective.importance}
					setObjectives={props.setObjectives}
				/>);
		}
	}

	// const mappedObjectives = props.objectives.map(objective => 
	// 	<Objective 
	// 		key={objective.id}
	// 		id={objective.id}
	// 		title={objective.title}
	// 		importance={objective.importance}
	// 		setObjectives={props.setObjectives}
	// 	/>);			


	return (
		<div>
			<ToolBar 
				handleCreateObjective={handleCreateObjective}
				searchObjectives={searchObjectives}
			/>
			<div className="objectives-container">
				{searchObjectives()}
			</div>
		</div>
	);
}

export default Objectives;
