import { useState, useContext } from "react";
import useAxios from "../../utils/useAxios";
import { AuthContext } from "../context/AuthContext";
import NavBar from "../components/Dashboard/Navbar/Navbar";

const Settings = () => {

    const { userAuth, logout } = useContext(AuthContext);
    const [formData, setFormData] = useState({username: userAuth.username, emailAddress: userAuth.emailAddress, password: ""});
    const [credentialsError, setCredentialsError] = useState("");
    const [formValidationErrors, setFormValidationErrors] = useState({emailAddress: "", password: ""});

    const baseUrl = "http://localhost:8080/api/users";

	const api = useAxios();

    const handleFormChange = (event) => {
        const {name, value} = event.target;
        setFormData( prevState => ({
            ...prevState,
            [name]: value 
        }));
    }

    const validateForm = (data) => {
        const errors = {emailAddress: "", username:"", password: "", passwordVerification:""};

        const emailPattern = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(\.\w{2,3})+$/;

        if (!data.emailAddress) {
            errors.emailAddress = "Email address is required";
        } 
        else if (!(emailPattern.test(data.emailAddress))) {
            errors.emailAddress = "Not a valid email address";
        }

        if (!data.username) {
            errors.username = "Username is required";
        } 
        else if (data.username.length < 3) {
            errors.username = "Username must be at least 3 characters long";
        }

        if (!data.password) {
            errors.password = "Password is required";
        } else if (data.password.length < 8) {
            errors.password = "Password must be at least 8 characters long";
        }

        setFormValidationErrors(errors);
        return errors;
    }

    const handleEditUser = async (event) => {

        event.preventDefault();

        const validationErrors = validateForm(formData);

        try {
            const response = await api.put(baseUrl, formData);

			props.setObjectives(prevState => ( 
				prevState.filter(objective => objective.id !== props.id)
					.concat(response.data)));

			setObjectiveIsChanged(false);

        } catch (error) {
            console.log(`Request failed: ${error.response.data.error_message}`);
		}

    }


	return (
        <div>
            <NavBar />

            <main className="settings-container">

                <h2>Account Settings</h2>

                <div className="user-box">

                    <h3>Edit username and email adress:</h3>

                    <form onSubmit={handleEditUser}>
                        <div>
                            <label className="input-label">Username:</label>
                            <input type="text" 
                            placeholder="Username"
                            name="username"
                            value={formData.username}
                            onChange={handleFormChange}
                            />
                            {formValidationErrors.username !== "" && <p id="user-error">{formValidationErrors.username}</p>}
                        </div>

                        <div>
                            <label className="input-label">Email address:</label>
                            <input type="text" 
                            placeholder="Email address"
                            name="emailAddress"
                            value={formData.emailAddress}
                            onChange={handleFormChange}
                            />
                            {formValidationErrors.emailAddress !== "" && <p id="user-error">{formValidationErrors.emailAddress}</p>}
                        </div>

                        <div> 
                            <label className="input-label">Current password:</label>
                            <input type="password" 
                            name="password"
                            value={formData.password}
                            onChange={handleFormChange}
                            />
                            {formValidationErrors.password !== "" && <p id="user-error">{formValidationErrors.password}</p>}
                        </div>

                        {credentialsError !== "" && <p id="user-error">{credentialsError}</p>}
                        <button>Save changes</button>
                    </form>
                </div>

            </main>
        </div>
	)

}

export default Settings;
