import { useState } from "react";
import axios from 'axios';

const Login = (props) => {

  const [emailAddress, setEmailAddress] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (event) => {
    event.preventDefault();
    loginService(emailAddress, password);
    getUsername(props.accessToken);
    setEmailAddress('');
    setPassword('');
  }

  const loginService = () => {
    const credentials = new URLSearchParams();
    credentials.append('username', emailAddress);
    credentials.append('password', password);

    const baseUrl = 'http://localhost:8080/login';
    const config = {
    	headers: {
    	  'Content-Type': 'application/x-www-form-urlencoded'
    	}
    }

    axios.post(baseUrl, credentials, config)
      .then(response => {
        if (response.status === 200) return response.data;
        else alert("error");
      })
      .then(data => {
        window.localStorage.setItem("access_token", data.access_token);
        props.setAccessToken(data.access_token);
        window.localStorage.setItem("refresh_token", data.refresh_token);
        props.setRefreshToken(data.refresh_token);
      })
      .catch((error) => {
      	console.log(`Error: ${error}`)
      })
  }

  const getUsername = (currentAccessToken) => {
    const token = `bearer ${currentAccessToken}`;
    console.log(`bearer ${currentAccessToken}`);
    const config = {
      headers: { Authorization: token },
    }
    const response = axios.get("/api/users/authenticated", config);
    return response.data.username;
  }

  const handleEmailAddressChange = (event) => {
    setEmailAddress(event.target.value);
  }
  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  }

	return (
		<div className="login-box">
			<h3>Welcome to Self.OKRs</h3>

			<form onSubmit={handleLogin}>
				<div>
					<input type="text" placeholder="Email address"
					value={emailAddress}
					onChange={handleEmailAddressChange}
					/>
				</div>
				<div> 
					<input type="password" placeholder="Password"
					value={password}
					onChange={handlePasswordChange}
					/>
				</div>
				<button type="submit">Log in</button>
			</form>

      <div><p>New to Self.OKRs? Create account</p></div>
		</div>
	)

}

export default Login;
