import NavBar from "./Navbar";
import Goals from "./Goals";
import { useState, useEffect } from "react";
import axios from 'axios';

const Dashboard = (props) => {

    const [username, setUsername] = useState('Username');

    const baseUrl = 'http://localhost:8080';

    useEffect(() => {
        const fetchedUsername = getUsername(props.accessToken);
        setUsername(fetchedUsername);
      }, [props.accessToken]);


    const getUsername = (currentAccessToken) => {
        const config = { headers: { 'Authorization': `Bearer ${currentAccessToken}` } };

        axios.get(`${baseUrl}/api/users/authenticated`, config)
            .then(response => { return response.data.username })
            .catch(error => { console.log('There was an error!', error) }
        );
    }

    return (
        <div>
			<NavBar 
                username={props.username} 
            />
            <Goals />

        </div>
    )
}

export default Dashboard;
