import { useEffect, useState } from "react";
import useAxios from "../../utils/useAxios";
import Objective from "./Objective";
import ToolBar from "./Toolbar";


const Objectives = (props) => {

	const [searchedObjectives, setSearchedObjectives] = useState(props.objectives);

	useEffect( () => {
        setSearchedObjectives(props.useEffect);
    }, [props.objectives]);

	const api = useAxios();

	const handleCreateObjective = async () => {

		const newObjectiveTemplate = {
			title: "Title", 
			importance: "1" }

		try {
            const response = await api.post("/objectives", newObjectiveTemplate);

			props.setObjectives(props.objectives.concat(response.data));

        } catch (error) {
            console.log(`Request failed: ${error.response.data.error_message}`);
		}
	}

	const sortFunction = (a, b) => {
		const fa = a.title.toLowerCase();
		const fb = b.title.toLowerCase();
        const ia = a.importance;
        const ib = b.importance;

        if (ia < ib) return 1;
        else if (ia > ib) return -1;
        else if (ia === ib) {
            if (fa < fb) return -1;
            if (fa > fb) return 1;
        }
		else return 0;
	}

