	// 					title={objective.title}
	// 					importance={objective.importance}
	// 					setObjectives={props.setObjectives}
	// 				/>);
	// 	}
	// }		

	const mappedSearchedObjectives = searchedObjectives
		.sort(sortFunction)
		.map(objective => 
			<Objective 
				key={objective.id}
				id={objective.id}
				title={objective.title}
				importance={objective.importance}
				setObjectives={props.setObjectives}
			/>);


	return (
		<div>
			<ToolBar 
				handleCreateObjective={handleCreateObjective}
				searchedObjectives={searchedObjectives}
				setSearchedObjectives={setSearchedObjectives}
			/>
			<div className="objectives-container">
				{mappedSearchedObjectives}
			</div>
		</div>
	);
}

export default Objectives;
